// src/components/Progress.js
import React from 'react'
import cx from 'classnames'
import { twMerge } from 'tailwind-merge'

const Progress = (props) => {
  const {
    value = 0,
    primary,
    secondary,
    success,
    warning,
    danger,
    rounded,
    showLabel,
    ...otherProps
  } = props


  const pct = Math.max(0, Math.min(100, Number(value) || 0))


  const trackClasses = twMerge(
    cx('relative w-full bg-gray-200 h-3', otherProps.className, {
      'rounded-full': rounded,
      'rounded': !rounded,
    })
  )

  const barClasses = cx('h-full transition-[width] duration-200', {

    'bg-blue-500': primary || (!secondary && !success && !warning && !danger),
    'bg-gray-900': secondary,
    'bg-green-500': success,
    'bg-orange-400': warning,
    'bg-red-600': danger,

    'rounded-full': rounded,
    'rounded': !rounded,
  })

  return (
    <div
      role="progressbar"
      aria-valuemin={0}
      aria-valuemax={100}
      aria-valuenow={pct}
      className={trackClasses}
      {...otherProps}
    >

      <div className={barClasses} style={{ width: `${pct}%` }} />


      {showLabel && (
        <span className="absolute inset-0 flex items-center justify-center text-xs font-semibold text-gray-800">
          {pct}%
        </span>
      )}
    </div>
  )
}

export default Progress
